import axios from "axios";

const AUTH_API_URL = 'http://localhost:8080/api/v1/auth/signin';

class UserService {
    signin(credentials) {
        return axios.post(AUTH_API_URL, credentials)
            .then(response => {
                // Xử lý phản hồi từ backend
                const token = response.data.token;
                // Lưu trữ token vào localStorage hoặc sessionStorage
                localStorage.setItem('token', token);
                return response;
            });
    }

    // Phương thức để đăng xuất (nếu cần)
    signout() {
        localStorage.removeItem('token');
        // Thực hiện các thao tác khác liên quan đến việc đăng xuất
    }

    // Phương thức để kiểm tra xem người dùng đã đăng nhập hay chưa
    isAuthenticated() {
        const token = localStorage.getItem('token');
        return token !== null;
    }
}

export default new UserService();
