import React, { useState } from 'react';
import UserService from '../services/UserService';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoginForm, setIsLoginForm] = useState(true); // Khởi tạo state cho biến isLoginForm

  const handleSubmit = async (e) => {
    e.preventDefault(); // Ngăn chặn trình duyệt gửi yêu cầu mặc định
    try {
      const response = await UserService.signin({ email, password });
      // Xử lý phản hồi từ backend
      console.log(response.data);
    } catch (error) {
      // Xử lý lỗi nếu có
      console.error('Error:', error);
    }
  };

  const toggleForm = () => {
    setIsLoginForm(!isLoginForm);
  };
  

  return (
    <form onSubmit={handleSubmit}>
      <h2>User Login</h2>
      <div className="mb-3">
        <label htmlFor="email" className="form-label">Email address</label>
        <input type="email" className="form-control" id="email" value={email} onChange={(e) => setEmail(e.target.value)}/>
      </div>
      <div className="mb-3">
        <label htmlFor="password" className="form-label">Password</label>
        <input
          type="password"
          className="form-control"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <button type="submit" className="btn btn-primary">Login</button>
      <p>Don't have an account? <span onClick={toggleForm}>Sign up</span></p>
    </form>
  );
};

export default LoginForm;
