import './App.css';
import React, { useState } from 'react';
import FooterComponent from './Component/FooterComponent';
import HeaderComponent from './Component/HeaderComponent';
import LoginForm from './Component/LoginForm';
import SignupForm from './Component/SigupFrom';
import VerifyEmail from './Component/VerifyEmailComponent'; 
import AdminComponent from './Admin/HeaderAdmin';

import Home from './Home';
import { BrowserRouter, Routes, Route } from 'react-router-dom';

function App() {

  const [userRole, setUserRole] = useState('');

  const handleLogin = (role) => {
    setUserRole(role);
  };

  return (
    <div className="App">
      <BrowserRouter>
        <HeaderComponent />
        <Routes>
          <Route path='/signin' element={<LoginForm onLogin={handleLogin} />} />
          <Route path='/signup' element={<SignupForm />} />
          <Route path="/verify" element={<VerifyEmail />} />
          <Route path="/admin" element={userRole === 'ADMIN' ? <AdminComponent /> : <Navigate to="/signin" />} />
        </Routes>
        <Home />
        <FooterComponent />
      </BrowserRouter>
    </div>
  );
}

export default App;
