import './App.css';
import React, { useState } from 'react';
import FooterComponent from './Component/FooterComponent';
import HeaderComponent from './Component/HeaderComponent';
import LoginForm from './Component/LoginForm';
import SignupForm from './Component/SigupFrom';
import VerifyEmail from './Component/VerifyEmailComponent'; 
import AdminComponent from './Admin/AdminComponent';

import Home from './Home';
import {BrowserRouter, Routes, Route} from 'react-router-dom'
function App() {

  const [userRole, setUserRole] = useState('');

  const handleLogin = (role) => {
    setUserRole(role);
  };

  return (
    <div className="App">

      <BrowserRouter>
      <HeaderComponent />
      <Routes>

         {/* <Route path='/api/v1/' element={<UserComponent />}></Route> */}

          <Route path='/signin' element={<LoginForm onLogin={handleLogin}/>}></Route>
          <Route path='/signup' element={<SignupForm />}></Route>
          <Route path="/verify" element={<VerifyEmail />}></Route>
          {userRole === 'ADMIN' && <Route path="/admin" element={<AdminComponent />}> </Route>}
      </Routes>
      <Home />
      <FooterComponent />
      </BrowserRouter>
    </div>
  );
}

export default App;
