import React, { useState } from 'react';
import LoginForm from '../Component/LoginForm.js'; // Đường dẫn đến file LoginForm của bạn

const HeaderComponent = () => {
  const [showLoginForm, setShowLoginForm] = useState(false);
  const [showSignupForm, setShowSignupForm] = useState(false);

  const toggleLoginForm = () => {
    setShowLoginForm(!showLoginForm);
    setShowSignupForm(false); // Ẩn form đăng ký khi hiển thị form đăng nhập
  };

  const toggleSignupForm = () => {
    setShowSignupForm(!showSignupForm);
    setShowLoginForm(false); // Ẩn form đăng nhập khi hiển thị form đăng ký
  };

  return (
    <header>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container-fluid">
          <a className="navbar-brand" href="/">Library Management System</a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item">
                <a className="nav-link" href="/books">Books</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/members">Members</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/borrowings">Borrowings</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/about">About</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/contact">Contact</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/api/v1/auth/signin" onClick={toggleLoginForm } >Login</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/" onClick={toggleSignupForm}>Sign Up</a>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </header>
  );
}

export default HeaderComponent;
