import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const VerifyEmail = () => {
  const [verificationStatus, setVerificationStatus] = useState('Xác minh email...');
  const { token } = useParams(); // Lấy mã token xác minh từ URL

  useEffect(() => {
    const verifyEmail = async () => {
      try {
        // Gửi yêu cầu xác minh email đến backend khi component được mount
        const response = await axios.put(`http://localhost:8080/api/v1/auth/verify?token=${token}`);
        setVerificationStatus(response.data); // Cập nhật trạng thái xác minh từ phản hồi của backend
      } catch (error) {
        console.error('Error:', error);
        setVerificationStatus('Xác minh email thất bại.');
      }
    };

    verifyEmail();
  }, [token]);

  return (
    <div>
      <h2>Xác minh Email</h2>
      <p>{verificationStatus}</p>
    </div>
  );
};

export default VerifyEmail;
