import React, { useState } from 'react';
import UserService from '../services/UserService';

const SignupForm = () => {
  const [firstname, setFirstName] = useState('');
  const [lastname, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault(); // Ngăn chặn trình duyệt gửi yêu cầu mặc định
    try {
      // Gửi yêu cầu đăng ký người dùng đến backend
      const response = await UserService.signup({ firstname, lastname, email, password });
      // Xử lý phản hồi từ backend
      console.log(response.data);
      window.location.href = '/api/v1/auth/signin';
    } catch (error) {
      // Xử lý lỗi nếu có
      console.error('Error:', error);
      setErrorMessage(error.response.data.message); // Lưu thông báo lỗi từ backend vào state error
    }
  };

  return (
    <div className="container">
      <h2>Sign Up</h2>
      {errorMessage && <div className="alert alert-danger">{errorMessage}</div>} {/* Hiển thị thông báo lỗi nếu có */}
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="firstname" className="form-label">First Name</label>
          <input type="text" className="form-control" id="firstname" value={firstname} onChange={(e) => setFirstName(e.target.value)} />
        </div>
        <div className="mb-3">
          <label htmlFor="lastname" className="form-label">Last Name</label>
          <input type="text" className="form-control" id="lastname" value={lastname} onChange={(e) => setLastName(e.target.value)} />
        </div>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email</label>
          <input type="email" className="form-control" id="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">Password</label>
          <input type="password" className="form-control" id="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
};

export default SignupForm;
